class FormDemo extends StatefulWidget {
  @override
  State createState() => _FormDemoState();
}

class _FormDemoState extends State {
  // Flutter中通过绑定GlobalKey获取StatefulWidge的State对象
  final registerFormKey = GlobalKey();
  String username, password;

  // 点击注册按钮时指定了onPressed为此方法
  void registerForm() {
    // 调用Form的State对象的save方法
    // 内部会调用Form中放入的TextFormField的onSave回调
    registerFormKey.currentState.save();

    // 调用Form的State的validate方法, 就会回调Form中各个子控件的validator
    // 如果在子控件中指定了: autovalidate: true
    // 则可以省略此句
    registerFormKey.currentState.validate();

    print("username:$username password:$password");
  }

  @override
  Widget build(BuildContext context) {
    return Form(
      // 指定Form表单的key为registerFormKey, 通过registerFormKey.currentState可以获取它的State对象
      key: registerFormKey,
      child: Column(
        mainAxisAlignment: MainAxisAlignment.center,
        children: [
          TextFormField(
            decoration: InputDecoration(
              icon: Icon(Icons.people),
              labelText: "用户名或手机号"
            ),
            onSaved: (value) {
              print("获取用户名或手机号: $value");
              this.username = value;
            },
            validator: (value) {
              if (value.isEmpty) {
                return "帐号不能为空";
              }
              return null;
            },
            // autovalidate: true,
          ),
          TextFormField(
            obscureText: true,
            decoration: InputDecoration(
              icon: Icon(Icons.lock),
              labelText: "密码"
            ),
            onSaved: (value) {
              print("获取密码: $value");
              this.password = value;
            },
            validator: (value) {
              if (value.isEmpty) {
                return "密码不能为空";
              }
              return null;
            },
            // autovalidate: true,
          ),
          SizedBox(height: 16,),
          Container(
            width: double.infinity,
            height: 44,
            child: RaisedButton(
              color: Colors.lightGreen,
              child: Text(
                "注册",
                style: TextStyle(fontSize: 20, color: Colors.white),
              ),
              onPressed: registerForm,
            ),
          )
        ],
      ),
    );
  }
}

