import 'dart:convert';

import 'package:flutter/material.dart';
import 'package:flutter/widgets.dart';
import 'package:flutter/services.dart' show rootBundle;

main(List args) {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      home: Scaffold(
        appBar: AppBar(
          title: Text("Hello World"),
        ),
        body: HomeBody(),
      ),
    );
  }
}

class HomeBody extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Column(
      children: [
        Image.asset("images/test.jpg"),
        FlatButton(
          child: Text('Click Me'),
          onPressed: () {
            testJson();
          },
        ),
      ],
    );
  }

  void testJson() async {
    List anchors = await Anchor().getAnchors();
    for (var o in anchors) {
      o.printInfo();
    }
  }
}

class Anchor {
  String nickName;
  String roomName;
  String imageUrl;

  Anchor({
    this.nickName,
    this.roomName,
    this.imageUrl
  });

  Anchor.withMap(Map parsedMap) {
    this.nickName = parsedMap["nick_name"];
    this.roomName = parsedMap["room_name"];
    this.imageUrl = parsedMap["image_url"];
  }

  void printInfo() {
    print("nickName: $nickName roomName: $roomName  imageUrl: $imageUrl");
  }

  Future> getAnchors() async {
    // 1. 读取json文件
    String jsonString = await rootBundle.loadString("resource/daliu.json");
    // 2.转成List或Map类型
    final jsonResult = json.decode(jsonString);
    // 3. 遍历List，并且转成Anchor对象放到另一个List中
    List anchors = new List();
    for (Map map in jsonResult) {
      anchors.add(Anchor.withMap(map));
    }
    return anchors;
  }
}

